void print1(uint8_t c) {
    if (c >= 32 && c <= 126 && c != '\\' && c != '"' && (c < '0' || c > '9')) {
        printf("%c", c);
    } else {
        printf("\\%d", c);
    }
}

void print_uint8(uint8_t *data, size_t len) {
    for (int i = 0; i < len; ++i) {
        print1(data[i]);
    }
}

void print_uint16(int16_t *data, size_t len) {
    for (int i = 0; i < len; ++i) {
        unsigned short d = data[i];
        print1(d & 0xff);
        print1(d >> 8);
    }
}

